from selenium import webdriverimport unittestimport timeLINK1 = "http://suninjuly.github.io/registration1.html"LINK2 = "http://suninjuly.github.io/registration2.html"class TestAbs(unittest.TestCase):    def setUp(self):        self.browser = webdriver.Chrome()       # self.browser.get(LINK)    def test_abs(self):        # Ваш код, который заполняет обязательные поля        required_elements = self.browser.find_elements_by_css_selector('input[required]')        input1 = self.browser.find_element_by_tag_name("input")        input1.send_keys("Ivan")        input2 = self.browser.find_element_by_css_selector('.form-control.second:required')        input2.send_keys("Petrov")        # sleep(10)        input3 = self.browser.find_element_by_css_selector('.form-control.third:required')        input3.send_keys("test@test.com")        # Отправляем заполненную форму        button = self.browser.find_element_by_css_selector("button.btn")        button.click()        # Проверяем, что смогли зарегистрироваться        # ждем загрузки страницы        time.sleep(3)        # находим элемент, содержащий текст        welcome_text_elt = self.browser.find_element_by_tag_name("h1")        # записываем в переменную welcome_text текст из элемента welcome_text_elt        welcome_text = welcome_text_elt.text        # с помощью assert проверяем, что ожидаемый текст совпадает с текстом на странице сайта        self.assertEqual("Congratulations! You have successfully registered!", welcome_text, "String are not Equal")    def tearDown(self):        # ожидание чтобы визуально оценить результаты прохождения скрипта        time.sleep(3)        # закрываем браузер после всех манипуляций        self.browser.quit()if __name__ == "__main__":    unittest.main()